<script type="text/javascript">
        let timeOut = 90;
        function idleLogout() {
            var t;
            window.onload = resetTimer;
            window.onmousemove = resetTimer;
            window.onmousedown = resetTimer;
            window.ontouchstart = resetTimer;
            window.onclick = resetTimer;
            window.onkeydown = resetTimer;
            window.addEventListener('scroll', resetTimer, true);

            function doReload() {
                window.location.reload(true);
            }

            function resetTimer() {
                clearTimeout(t);
                let seconds  = (timeOut-1) * 60 * 1000;
                t = setTimeout(doReload, seconds );
            }
        }
    function showToast(msg) {
        let toast = document.getElementById('toast');
        toast.innerText = msg;
        toast.style.display = 'block';
        setTimeout(() => {
            toast.style.display = 'none'
        },6000)
    }
        idleLogout();
        showToast('Atenção: Seu limite de tentativas está quase acabando, sugerimos que caso não lembre da sua senha, solicite uma nova através do acesso "Esqueceu sua senha?".')
    </script>
